const extractedDataArray = [];

function pushData(
  insuranceCompany,
  requestReferenceNo,
  insuranceCompanyCode,
  deductibleAmountArray,
  taxableAmountArray,
  BasePremiumAmountArray,
  policyPremiumArray
) {
  extractedDataArray.push({
    insuranceCompany: insuranceCompany,
    requestReferenceNo: requestReferenceNo,
    insuranceCompanyCode: insuranceCompanyCode,
    deductibleAmountArray: deductibleAmountArray,
    taxableAmountArray: taxableAmountArray,
    BasePremiumAmountArray: BasePremiumAmountArray,
    policyPremiumArray:policyPremiumArray
  });
}

jsonData.body.forEach((item) => {
  Object.keys(item).forEach((insuranceCompany) => {
    const companyDataString = item[insuranceCompany];
    const companyData = JSON.parse(companyDataString);
    const requestReferenceNo = companyData.RequestReferenceNo;
    const insuranceCompanyCode = companyData.InsuranceCompanyCode;

    const compQuotes = companyData.CompQuotes;
    const deductibleAmountArray = [];
    const taxableAmountArray = [];
    const BasePremiumAmountArray = [];
    const policyPremiumArray = [];
    compQuotes.forEach((quote) => {
        const policyTitleID = quote.PolicyTitleID;
       // console.log("Sdsdf" ,policyTitleID)
      const quoteReferenceNo = quote.Deductibles;
      quoteReferenceNo.forEach((deductible) => {
        const deductibleAmt = deductible.DeductibleAmount;
        deductibleAmountArray.push(deductibleAmt);
        taxableAmountArray.push(deductible.TaxableAmount);
        BasePremiumAmountArray.push(deductible.BasePremium);
        
      });
      const quotePolicy = quote.PolicyPremiumFeatures;
      quotePolicy.forEach((feature)=>{
        // console.log(feature);
        policyPremiumArray.push(feature);
      });
      // console.log("deuctibles",quoteReferenceNo)
    // console.log(policyPremiumArray)
    });

    pushData(
      insuranceCompany,
      requestReferenceNo,
      insuranceCompanyCode,
      deductibleAmountArray,
      taxableAmountArray,
      BasePremiumAmountArray,
      policyPremiumArray
    );
  });
});

let selectedOptions = {};
let selectedInsComp='';

function updateVariable(amount , id , companycode) {
    

    const checkboxElement = document.getElementById(id);
    // console.log("frf",checkboxElement.checked);
    const sumValueElement = document.getElementById(`sumValue-${companycode}`);
    
    selectedInsComp = companycode;

    if(selectedOptions[companycode] == null)
    selectedOptions[companycode ]= 0;
    
    
    if (checkboxElement.checked) {
        selectedOptions[companycode] = selectedOptions[companycode]+ amount;
    
        
    }
    else { 
        selectedOptions[companycode] = selectedOptions[companycode]- amount;
       
    }
    
    sumValueElement.textContent = "+"+selectedOptions[companycode]+" SAR(VAT)";
}





